import bag from '../assets/graphics/bag.svg';
import '../App.css';
import { useState, useEffect } from 'react';
import { useSelector} from 'react-redux';
import { useDispatch } from 'react-redux';
import { addAllItems,addNewItem } from '../actions/addCartItem';
import {Link} from 'react-router-dom';
function Menu()
{
  const airItems = useSelector((state) => {return state.airItems});
  const cartItems = useSelector((state) => {return state.airItems})  
  //id och antal knytet till id
  const dispatch = useDispatch();

const [cartN,setCartN] = useState(0);
    useEffect(() => {
        async function getItems() 
        {
          const response = await fetch("http://192.168.1.2:5000/api/beans");
          const data = await response.json();
          dispatch(addAllItems(data.menu));
        }
        getItems();
      }, []);
      useEffect(() => {
        async function getCartNum() 
        {
         /*  {cartItems.map((item) => {
            setCartN(cartN+item.num);
          })} */
        }
        getCartNum();
      }, []);

      function addItem(event)
      {
        console.log(event.target.id)
         addNewItem(event.target.id);
         console.log(cartItems)
         //kolla koden i m6 skriver över airItems
         setCartN(cartN+1);
      }

    return (
        <div class="Menu">
            <Link to="/cart"><button class="Bag"><img class="bag" src={bag}></img><div class="bagItems">{cartN}</div></button></Link>
            <div class="MenuHead">Meny</div>
     {airItems.map((item) => {
          return  <div><button onClick={addItem} id={item.id} class="button3">+</button><div class="mItem">{item.title} {item.price}Kr</div>{item.desc}</div>
        })}


        </div>

    )
}
export default Menu;
