import { bindActionCreators } from "redux";

const intialState = {
    cartItems:[],
    OrderC:[]
}

const cartReducer = (state = intialState,action) => 
{
    switch(action.type)
    {
        case 'ADD_ALL_ITEMS':
        return {
            ...state,
            cartItems: action.payload
        }
        case 'ADD_NEW_ORDER':
            return {
            cartItems: [
                ...state.cart,
                {
                    id: state.cartItems,
                    NumberOfItems:state.cartItems+1
                }

            ]}
            case 'RM_ORDER':
                return {
                cartItems: [
                    ...state.cart,
                    {
                        id: state.cartItems,
                        NumberOfItems:state.cartItems-1
                    }

                ]}    
        case 'ADD_NEW_ORDER_CONF':
            return {
                OrderC: [
                    ...state.cart,
                    {
                        OrderNr: state.order,
                        eta:state.eta
                    }
                ]} 
        default:
            return state;
    }
}

export default cartReducer;
