import { bindActionCreators } from "redux";

const cartState = {

    cartItems: [{ id: 1, num: 0 }],
}
const orderState = {

    orderC: [{ eta: 1, orderNr: 1 }]
}
const airState = {

    airItems: [],
}

const cartReducer = (state1 = cartState,state2 = orderState,state3 = airState, action) => {
    switch (action.type) {
        case 'ADD_ALL_CART_ITEMS':
            return {
                ...state1,
                cartItems: action.payload
            }
        case 'ADD_ALL_ITEMS':
            return {
                ...state3,
                airItems: action.payload
            }
        case 'ADD_NEW_ITEM':
            return {
                cartItems: [
                    ...state1.cartItems,
                    {
                        id: action.payload,
                        num: state.cartItems.num+1
                    }

                ]
            }
        case 'DEL_ORDER':
            return {
                cartItems: [
                    ...state1.cartItems,
                    {
                        id: action.payload.id,
                        num: state.cartItems.num - 1
                    }

                ]
            }
        case 'ADD_ORDER':
            return {
                cartItems: [
                    ...state1.cartItems,
                    {
                        id: action.payload.id,
                        num: state.cartItems.num + 1
                    }

                ]
            }
        case 'ADD_NEW_ORDER_CONF':
            return {
                ...state2,
                orderC: action.payload
            }
        default:
            return state;
    }
}

export default cartReducer;
