import { useDispatch } from 'react-redux';
import { addAllItems,addNewItem } from '../actions/addCartItem';


function Menu()
{
  const airItems = useSelector((state) => {return state.airItems});  
  const cartItems = useSelector((state) => {return state.cartItems});

  const dispatch = useDispatch();

    const [items, setItems] = useState(
        [  ]);
    useEffect(() => {
        async function getItems() {
          const response = await fetch("http://192.168.1.2:5000/api/beans");
          const data = await response.json();
          console.log("från api", data)
          dispatch(addAllItems(data.menu));
        }
        getItems();
      }, []);

    return (
        <div class="Menu">
            <a href="/cart"><button class="Bag"><img class="bag" src={bag}></img><div class="bagItems">{cartItems.num}</div></button></a>
            <div class="MenuHead">Meny</div>
     {airItems.map((item) => {
          return  <div><button class="button3">+</button><div class="mItem">{item.title} {item.price}Kr</div>{item.desc}</div>
        })}


        </div>

    )
}
export default Menu;
