import { bindActionCreators } from "redux";

const intialState = {
    cartItems:[{id:2,num:2},{id:1,num:4},{id:4,num:1}],
    airItems: [],
    orderC: []
}


const cartReducer = (state = intialState, action) => {
    switch (action.type) {
        case 'ADD_ALL_CART_ITEMS':
            return {
                ...state.cartItems,
                cartItems: action.payload
            }
        case 'ADD_ALL_ITEMS':
            return {
                ...state,
                airItems: action.payload
            }
        case 'ADD_NEW_ITEM':
            return {
                cartItems: [
                    ...state.cartItems,
                    {
                        id: action.payload

                    }

                ]}
        case 'DEL_ORDER':
            return {
                cartItems: [
                    ...state.cartItems,
                    {
                        id: action.payload.id,
                        num: 1
                    }

                ]
            }

        case 'ADD_NEW_ORDER_CONF':
            return {
                ...state,
                orderC: action.payload
            }
        default:
            return state;
    }
}

export default cartReducer;
