import bag from '../assets/graphics/bag.svg';
import '../App.css';
import { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import { useDispatch } from 'react-redux';
import { addAllItems, addNewItem, incCartItem } from '../actions/addCartItem';
import { Link } from 'react-router-dom';
function Menu() {
  const airItems = useSelector((state) => { return state.airItems });
  const cartItems = useSelector((state) => { return state.cartItems })
  //id och antal knytet till id
  const dispatch = useDispatch();

  let [cartN, setCartN] = useState(0);
  useEffect(() => {
    async function getItems() {
      const response = await fetch("http://192.168.1.2:5000/api/beans");
      const data = await response.json();
      dispatch(addAllItems(data.menu));
    }
    getItems();
  }, []);

    // function getCartNum() {
    //   let numIt = 0;
    //   cartItems.map((item) => {
    //     numIt += item.num;
    //   })
    //   setCartN(numIt)
    // }
    // getCartNum();


  function addItem(event) {
    getCartNum();
    let orderExist = false;
    cartItems.map((item) => {
      if (event.target.id == item.id)
        orderExist = true;
    })
    if (orderExist) {
      dispatch(incCartItem(event.target.id));
    }
    else {
      dispatch(addNewItem(event.target.id));
    }

  }

  return (
    <div class="Menu">
      <Link to="/cart"><button class="Bag"><img class="bag" src={bag}></img><div class="bagItems">{cartN}</div></button></Link>
      <div class="MenuHead">Meny</div>
      {airItems.map((item) => {
        return <div><button onClick={addItem} id={item.id} class="button3">+</button><div class="mItem">{item.title} {item.price}Kr</div>{item.desc}</div>
      })}
      <div>Göteborg fyller 400 år Köp en bryggkaffe och en gustav adolfs bakelse och få 49kr rabatt </div>

    </div>

  )
}
export default Menu;
