import bag from '../assets/graphics/bag.svg';
import '../App.css';
import { useState, useEffect } from 'react';
import React from 'react';
import ReactDOM from 'react-dom';
import Modal from 'react-modal';
function Menu()
{
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
    const [items, setItems] = useState(
        [  ]);
    useEffect(() => {
        async function getItems() {
          const response = await fetch("http://192.168.1.2:5000/api/beans");
          const data = await response.json();
          console.log("från api", data)
          setItems(data.menu);
        }
        getItems();
      }, []);

    return (
        <div class="Menu">
            <button class="Bag"><img class="bag" src={bag}></img><div class="bagItems"></div></button>
            <div class="MenuHead">Meny</div>
  <>
      <button variant="primary" onClick={handleShow}>
        Launch demo modal
      </button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
        <Modal.Footer>
          <button variant="secondary" onClick={handleClose}>
            Close
          </button>
          <button variant="primary" onClick={handleClose}>
            Save Changes
          </button>
        </Modal.Footer>
      </Modal>
    </>
     {items.map((item) => {
          return  <div><button class="button3">+</button><div class="mItem">{item.title} {item.price}Kr</div>{item.desc}</div>
        })}


        </div>

    )
}
export default Menu;
