import { bindActionCreators } from "redux";

const intialState = {
    airItems: [],
    cartItems: [{id:1,num:0}],
    orderC: [{eta:1,orderNr:1}]
}

const cartReducer = (state = intialState, action) => {
    switch (action.type) {
        case 'ADD_ALL_CART_ITEMS':
            return {
                ...state,
                cartItems: action.payload
            }
            case 'ADD_ALL_ITEMS':
                return {
                    ...state,
                    airItems: action.payload
                }
        case 'ADD_NEW_ORDER':
            return {
                cartItems: [
                    ...state.cartItems,
                    {
                        id: action.payload,
                        NumberOfItems: state.cartItems + 1
                    }

                ]
            }
        case 'RM_ORDER':
            return {
                cartItems: [
                    ...state.cartItems,
                    {
                        id: action.payload.id,
                        NumberOfItems: state.cartItems.num - 1
                    }

                ]
            }
        case 'ADD_NEW_ORDER_CONF':
            return {
                ...state,
                orderC: action.payload
            }
        default:
            return state;
    }
}

export default cartReducer;
