import { bindActionCreators } from "redux";

const intialState = {
    airItems: [],
    cartItems: [],
    orderC: []
}

const cartReducer = (state = intialState, action) => {
    switch (action.type) {
        case 'ADD_ALL_CART_ITEMS':
            return {
                ...state,
                cartItems: action.payload
            }
        case 'ADD_NEW_ORDER':
            return {
                cartItems: [
                    ...state.cartItems,
                    {
                        id: action.payload,
                        NumberOfItems: state.cartItems + 1
                    }

                ]
            }
        case 'RM_ORDER':
            return {
                cartItems: [
                    ...state.cartItems,
                    {
                        id: action.payload,
                        NumberOfItems: state.cartItems - 1
                    }

                ]
            }
        case 'ADD_NEW_ORDER_CONF':
            console.log(orderC);
            return {
                ...state,
                orderC: action.payload
            }
        default:
            return state;
    }
}

export default cartReducer;
