import '../App.css';

import Modal from 'react-modal';
import { useState } from 'react';
import Status from './Status';
import { addNewOrderC } from '../actions/addCartItem';
import { useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { useDispatch } from 'react-redux';
import { addAllItems, addNewItem } from '../actions/addCartItem';
import arrowU from '../assets/graphics/arrow-up.svg';
import arrowD from '../assets/graphics/arrow-down.svg';

function Usedispatch() {
  const dispatch = useDispatch();
}

function Cart() {

  const [isOpen, setIsOpen] = useState(true);
  const Navigation = useNavigate();
  const dispatch = useDispatch();

  function toggleModal() {
    setIsOpen(!isOpen);
    Navigation('/menu');
  }

  let Submit = async (e) => {
    e.preventDefault();
    try {
      let res = await fetch("http://192.168.1.2:5000/api/beans", { method: "POST" });
      let resJson = await res.json();
      let eta = resJson.eta;
      //document.getElementsByTagName('Order').id; //placera i array och skicka,se ifall man kan få response med order från servern tillbaka med
      // document.getElementsByTagName('Order').num;
      let orderNr = resJson.orderNr;
      dispatch(addNewOrderC(resJson));
      console.log(resJson);
      if (res.status === 200) {
      } else {
      }
    } catch (err) {
    }
    Navigation('/status');
  };
  const airItems = useSelector((state) => { return state.airItems });
  const cartItems = useSelector((state) => { return state.cartItems });
  const [totalPrice, setTotalPrice] = useState(0);
  function increaseO() {

  }
  function decreaseO() {

  }

  let tprice = 0;
  function Order(props) {

    let returnV = '';
    let price = 0;
    airItems.map((item) => {

      if (props.id == item.id) {

        price = (item.price * props.num);

        returnV = <div ><div><div class="cartItem">{item.title}<div class="IncDecr"><button class="button5" onClick={increaseO}><img src={arrowU} /></button>
          {props.num}<button class="button5" onClick={decreaseO}><img src={arrowD} /></button></div></div></div>
        <br></br>         <br></br> 
        <br></br> 

        <div class="itemsPrice">
          {item.price * props.num} kr</div></div>


      }

    })
    tprice=tprice+price;
    setTotalPrice(tprice/2)
    return returnV
  }

  return (

    <div>
      <Modal
        isOpen={isOpen}
        onRequestClose={toggleModal}
        contentLabel="Cart"
        className="Cart"
        overlayClassName="CartO"
        closeTimeoutMS={500}
      >

        <button class="button4" onClick={toggleModal} ></button>
        <h2 style={{ textAlign: "center" }}>Din beställning</h2>
        {cartItems.map((item) => {
          return <Order id={item.id} num={item.num}></Order>
        }

        ) }



        <div class="orderTotal">Total<div style={{float: "right"}} >{totalPrice} kr</div> </div><div class="moms">inkl moms + drönarleverans</div>
        <div style={{ textAlign: "center" }} > <button class="button2" onClick={Submit}>Take my money!</button></div>
      </Modal></div>
  )
  //fixa modal över menu och inte separat

  //fixa så den hämtar cartitems  och med id hämtar items och skriver ut namn antal priset 
  //för antalet och total priset med knappar som öka och minskar antalet när det är nere på 0 försvinner produkten
}
export default Cart;
