import { PureComponent } from 'react'

import Action from '../injectors';

import { Payload, Props } from './types'

import { injectArgs } from '@angular/core/esm5/src/di/injector';

export default class Store extends PureComponent {
  __source = 'payload.states'
  __currentInjector = (payload: Payload) => {
    return Action(injectArgs(payload.states))
  }
  constructor(props: Props) {
    super(props)
  }
}